Time()}`):/*#__PURE__*/_jsxs("div",{className:"empty-state",children:[/*#__PURE__*/_jsx("span",{className:"empty-icon",children:"\uD83C\uDF69"}),/*#__PURE__*/_jsx("p",{children:"No data available"})]})})]})]}),/*#__PURE__*/_jsxs("div",{className:"dashboard-row analysis-row",children:[/*#__PURE__*/_jsx("div",{className:"protocol-widget-container",children:/*#__PURE__*/_jsx(ProtocolWidget,{threatStats:data.threatStats,loading:refreshing})}),/*#__PURE__*/_jsxs("div",{className:"chart-card",children:[/*#__PURE__*/_jsx("div",{className:"chart-header",children:/*#__PURE__*/_jsx("h3",{children:"Attack Vectors"})}),/*#__PURE__*/_jsx("div",{className:"chart-container",children:generateAttackVectorData()?/*#__PURE__*/_jsx(Bar,{data:generateAttackVectorData(),options:chartOptions},`attack-vectors-${lastUpdated.getTime()}`):/*#__PURE__*/_jsxs("div",{className:"empty-state",children:[/*#__PURE__*/_jsx("span",{className:"empty-icon",children:"\uD83D\uDEE1\uFE0F"}),/*#__PURE__*/_jsx("p",{children:"No data available"})]})})]})]}),/*#__PURE__*/_jsxs("div",{className:"dashboard-row activity-row",children:[/*#__PURE__*/_jsxs("div",{className:"activity-card",children:[/*#__PURE__*/_jsxs("div",{className:"activity-header",children:[/*#__PURE__*/_jsx("h3",{children:"Live Activity Feed"}),/*#__PURE__*/_jsx("div",{className:"activity-controls",children:/*#__PURE__*/_jsxs("span",{className:"activity-count",children:[Math.min(activityFeed.length,4)," recent events"]})})]}),/*#__PURE__*/_jsx("div",{className:"activity-feed",children:activityFeed.length===0?/*#__PURE__*/_jsxs("div",{className:"empty-state",children:[/*#__PURE__*/_jsx("span",{className:"empty-icon",children:"\uD83D\uDCCA"}),/*#__PURE__*/_jsx("p",{children:"No recent activity"})]}):/*#__PURE__*/_jsxs(_Fragment,{children:[activityFeed.slice(0,4).map((item,index)=>/*#__PURE__*/_jsx(ActivityItem,{item:item,onClick:()=>navigate(`/threats/alerts/${item.id}`)},index)),activityFeed.length>4&&/*#__PURE__*/_jsx("div",{className:"view-mor