export const dispatch = (action: Action, toasterId = DEFAULT_TOASTER_ID) => {
  memoryState[toasterId] = reducer(
    memoryState[toasterId] || defaultToasterState,
    action
  );
  listeners.forEach(([id, listener]) => {
    if (id === toasterId) {
      listener(memoryState[toasterId]);
    }
  });
